CREATE OR REPLACE FUNCTION pls_obter_valor_ir ( nr_seq_benef_ir_p pls_mens_beneficiario_ir.nr_sequencia%type, dt_referencia_p timestamp, ie_tipo_valor_p text) RETURNS bigint AS $body$
DECLARE


vl_mensalidade_w		pls_mens_detalhe_ir.vl_mensalidade%type;
vl_coparticipacao_w		pls_mens_detalhe_ir.vl_coparticipacao%type;
vl_reembolso_w			pls_mens_detalhe_ir.vl_reembolso%type;
vl_desconto_w			pls_mens_detalhe_ir.vl_desconto%type;
vl_devolucao_w			pls_mens_detalhe_ir.vl_devolucao%type;


BEGIN

select	sum(vl_mensalidade),
	sum(vl_coparticipacao),
	sum(vl_reembolso),
	sum(vl_desconto),
	sum(vl_devolucao)
into STRICT	vl_mensalidade_w,
	vl_coparticipacao_w,
	vl_reembolso_w,
	vl_desconto_w,
	vl_devolucao_w
from	pls_mens_detalhe_ir
where	nr_seq_beneficiario_ir	= nr_seq_benef_ir_p
and	dt_baixa between trunc(dt_referencia_p,'month') and fim_dia(last_day(dt_referencia_p));

if (ie_tipo_valor_p = 'M') then
	return	coalesce(vl_mensalidade_w,0);
elsif (ie_tipo_valor_p = 'C') then
	return	coalesce(vl_coparticipacao_w,0);
elsif (ie_tipo_valor_p = 'R') then
	return	coalesce(vl_reembolso_w,0);
elsif (ie_tipo_valor_p = 'D') then
	return	coalesce(vl_desconto_w,0);
elsif (ie_tipo_valor_p = 'V') then
	return	coalesce(vl_devolucao_w,0);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
