SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valor_item_boleto ( nr_seq_mensalidade_p bigint, nr_ordem_p bigint, ie_tipo_item_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision	:= null;
nr_sequencia_w			bigint;
nr_ordem_w			bigint	:=0;
nr_seq_contrato_w		bigint;
nr_seq_segurado_w		bigint;
ie_ordem_w			bigint;

/*
ie_tipo_item_p
'C' - Consulta
'E' - Exames
'TR'- Tratamento
'TO' - Total
*/
c01 CURSOR FOR
	SELECT	1 ie_ordem,
		a.nr_sequencia
	from	pls_contrato_pagador c,
		pessoa_fisica	b,
		pls_segurado	a
	where	b.cd_pessoa_fisica	= a.cd_pessoa_fisica
	and	a.cd_pessoa_fisica	= c.cd_pessoa_fisica
	and	c.nr_seq_contrato	= a.nr_seq_contrato
	and	a.nr_seq_contrato	= nr_seq_contrato_w
	
union all

	SELECT	2 ie_ordem,
		a.nr_sequencia
	from	pessoa_fisica	b,
		pls_segurado	a
	where	b.cd_pessoa_fisica	= a.cd_pessoa_fisica
	and	a.nr_seq_contrato	= nr_seq_contrato_w
	and	not exists (select	1
				from	pls_contrato_pagador x
				where	x.nr_seq_contrato	= a.nr_seq_contrato
				and	x.cd_pessoa_fisica	= a.cd_pessoa_fisica)
	order by ie_ordem;


BEGIN

select	max(nr_seq_contrato)
into STRICT	nr_seq_contrato_w
from	pls_mensalidade a
where	a.nr_sequencia	= nr_seq_mensalidade_p;

open c01;
loop
fetch c01 into
	ie_ordem_w,
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	nr_ordem_w := nr_ordem_w + 1;

	if (nr_ordem_w = nr_ordem_p) then
		if (ie_tipo_item_p = 'E') then
			select	coalesce(sum(c.vl_coparticipacao),0)
			into STRICT	vl_retorno_w
			from	procedimento e,
				pls_conta_proc d,
				pls_conta_coparticipacao c,
				pls_mensalidade_segurado a
			where	a.nr_sequencia	= c.nr_seq_mensalidade_seg
			and	c.nr_seq_conta_proc	= d.nr_sequencia
			and	d.cd_procedimento	= e.cd_procedimento
			and	d.ie_origem_proced	= e.ie_origem_proced
			and	a.nr_seq_mensalidade	= nr_seq_mensalidade_p
			and	a.nr_seq_segurado	= nr_seq_segurado_w
			and	e.cd_grupo_proc in (0);
		elsif (ie_tipo_item_p = 'C') then
			select	coalesce(sum(c.vl_coparticipacao),0)
			into STRICT	vl_retorno_w
			from	procedimento e,
				pls_conta_proc d,
				pls_conta_coparticipacao c,
				pls_mensalidade_segurado a
			where	a.nr_sequencia	= c.nr_seq_mensalidade_seg
			and	c.nr_seq_conta_proc	= d.nr_sequencia
			and	d.cd_procedimento	= e.cd_procedimento
			and	d.ie_origem_proced	= e.ie_origem_proced
			and	a.nr_seq_mensalidade	= nr_seq_mensalidade_p
			and	a.nr_seq_segurado	= nr_seq_segurado_w
			and	e.cd_grupo_proc in (0);
		elsif (ie_tipo_item_p = 'TR') then
			select	coalesce(sum(c.vl_coparticipacao),0)
			into STRICT	vl_retorno_w
			from	procedimento e,
				pls_conta_proc d,
				pls_conta_coparticipacao c,
				pls_mensalidade_segurado a
			where	a.nr_sequencia	= c.nr_seq_mensalidade_seg
			and	c.nr_seq_conta_proc	= d.nr_sequencia
			and	d.cd_procedimento	= e.cd_procedimento
			and	d.ie_origem_proced	= e.ie_origem_proced
			and	a.nr_seq_mensalidade	= nr_seq_mensalidade_p
			and	a.nr_seq_segurado	= nr_seq_segurado_w
			and	e.cd_grupo_proc in (0);
		elsif (ie_tipo_item_p = 'TO') then
			select	coalesce(sum(c.vl_coparticipacao),0)
			into STRICT	vl_retorno_w
			from	procedimento e,
				pls_conta_proc d,
				pls_conta_coparticipacao c,
				pls_mensalidade_segurado a
			where	a.nr_sequencia	= c.nr_seq_mensalidade_seg
			and	c.nr_seq_conta_proc	= d.nr_sequencia
			and	d.cd_procedimento	= e.cd_procedimento
			and	d.ie_origem_proced	= e.ie_origem_proced
			and	a.nr_seq_mensalidade	= nr_seq_mensalidade_p
			and	a.nr_seq_segurado	= nr_seq_segurado_w;
		end if;
	end if;
	end;
end loop;
close c01;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_valor_item_boleto ( nr_seq_mensalidade_p bigint, nr_ordem_p bigint, ie_tipo_item_p text) FROM PUBLIC;

