SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valor_mens_repasse ( nr_seq_mensalidade_p bigint, nr_seq_repasse_mens_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE



/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Obter os valores das mensalidades.
ie_opcao_p:
	V - Valor da mensalidade
	VP - Valor pré-estabelecido
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x] Tasy (Delphi/Java) [   ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_mensalidade_w 		pls_mensalidade_segurado.vl_mensalidade%type;
ie_cancelamento_w		pls_repasse_mens.ie_cancelamento%type;
vl_pre_estab_w			pls_mensalidade_segurado.vl_pre_estabelecido%type;
vl_retorno_w			double precision;


BEGIN

if (ie_opcao_p = 'V') then
	begin
		select	a.vl_mensalidade
		into STRICT	vl_mensalidade_w
		from	pls_mensalidade_segurado a
		where	a.nr_sequencia	= nr_seq_mensalidade_p;

		select 	ie_cancelamento
		into STRICT	ie_cancelamento_w
		from	pls_repasse_mens
		where	nr_sequencia	= nr_seq_repasse_mens_p;

		if (coalesce(ie_cancelamento_w::text, '') = '' or ie_cancelamento_w = 'C') then
			vl_retorno_w := vl_mensalidade_w;

		else
			vl_retorno_w := (vl_mensalidade_w * -1);
		end if;
	exception
	when others then
		vl_mensalidade_w	:= 0;
		ie_cancelamento_w	:= null;
		vl_retorno_w 		:= 0;
	end;
end if;

if (ie_opcao_p = 'VP') then
	begin
		select	substr(pls_obter_valor_item_mens(a.nr_sequencia,'1'),1,100)
		into STRICT	vl_pre_estab_w
		from	pls_mensalidade_segurado a
		where	a.nr_sequencia	= nr_seq_mensalidade_p;

		select 	ie_cancelamento
		into STRICT	ie_cancelamento_w
		from	pls_repasse_mens
		where	nr_sequencia	= nr_seq_repasse_mens_p;

		if (coalesce(ie_cancelamento_w::text, '') = '' or ie_cancelamento_w = 'C') then
			vl_retorno_w := vl_pre_estab_w;

		else
			vl_retorno_w := (vl_pre_estab_w * -1);
		end if;
	exception
	when others then
		vl_pre_estab_w		:= 0;
		ie_cancelamento_w	:= null;
		vl_retorno_w 		:= 0;
	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
