SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valor_prorata ( nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_retorno_w		double precision	:= 0;


BEGIN 
 
select	coalesce(sum(b.vl_recebido),0) vl_titulo 
into STRICT	vl_retorno_w 
from	titulo_receber		t, 
    titulo_rec_liq_cc	b, 
    titulo_receber_liq	a 
where	b.nr_titulo		= a.nr_titulo 
and	a.nr_titulo		= t.nr_titulo 
and	b.nr_seq_baixa		= a.nr_sequencia 
and	(b.cd_conta_rec_pls IS NOT NULL AND b.cd_conta_rec_pls::text <> '') 
and	(b.vl_antecipacao_mens IS NOT NULL AND b.vl_antecipacao_mens::text <> '') 
and	t.ie_situacao not in ('3') 
and   t.nr_titulo = nr_titulo_p 
group by t.dt_vencimento, 
    b.cd_conta_rec_pls, 
    t.nr_titulo;
	 
if (vl_retorno_w = 0) or (coalesce(vl_retorno_w::text, '') = '')then 
	select	coalesce(sum(b.vl_recebido),0) vl_titulo 
	into STRICT	vl_retorno_w 
	from	titulo_receber		t, 
		titulo_rec_liq_cc	b, 
		titulo_receber_liq	a 
	where	b.nr_titulo		= a.nr_titulo 
	and	a.nr_titulo		= t.nr_titulo 
	and	b.nr_seq_baixa		= a.nr_sequencia 
	--and	b.cd_conta_rec_pls	is not null 
	and	(b.vl_antecipacao_mens IS NOT NULL AND b.vl_antecipacao_mens::text <> '') 
	and	t.ie_situacao not in ('3') 
	and   t.nr_titulo = nr_titulo_p 
	group by t.dt_vencimento, 
		b.cd_conta_rec_pls, 
		t.nr_titulo;
end if;
 
return	coalesce(vl_retorno_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
