CREATE OR REPLACE FUNCTION pls_obter_valor_protocolo_imp ( nr_seq_protocolo_p pls_protocolo_conta_imp.nr_sequencia%type, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/*
Opções:
C - Valor total geral da conta
*/
ds_retorno_w	double precision;


BEGIN

if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then

	if (ie_opcao_p = 'C') then
		select	coalesce(sum(a.vl_total_geral),0)
		into STRICT	ds_retorno_w
		from	pls_conta_imp a
		where	a.nr_seq_protocolo = nr_seq_protocolo_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
