CREATE OR REPLACE FUNCTION pls_obter_valor_recalc ( nr_seq_conta_p pls_conta.nr_sequencia %type, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

ds_retorno_w	:= 0;

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then

		begin
			select 	CASE WHEN ie_opcao_p=1 THEN  sum(vl_liberado)  ELSE sum(vl_lib_original) END
			into STRICT	ds_retorno_w
			from (	SELECT	coalesce(vl_liberado, 0) vl_liberado,
						coalesce(vl_lib_original, 0) vl_lib_original
					from	pls_conta_proc
					where	nr_seq_conta = nr_seq_conta_p
					
union all

					SELECT	coalesce(vl_liberado, 0) vl_liberado,
						coalesce(vl_lib_original, 0) vl_lib_original
					from	pls_conta_mat
					where	nr_seq_conta = nr_seq_conta_p) alias7;
		exception
		when others then
			ds_retorno_w := 0;
		end;

end if;

return	campo_mascara_virgula(ds_retorno_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
