CREATE OR REPLACE FUNCTION pls_obter_valor_receita_mes ( dt_mes_p timestamp, nr_seq_contrato_p bigint, ie_tipo_valor_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;


BEGIN
if (ie_tipo_valor_p = 'A') then
	if (coalesce(nr_seq_contrato_p,0) = 0) then
		select	coalesce(sum(vl_mensalidade),0) vl_receita
		into STRICT	vl_retorno_w
		from	pls_mensalidade_segurado
		where	trunc(dt_mesano_referencia, 'Month') = trunc(dt_mes_p,'Month');
	elsif (coalesce(nr_seq_contrato_p,0) > 0) then
		select	coalesce(sum(vl_mensalidade),0) vl_receita
		into STRICT	vl_retorno_w
		from	pls_segurado			b,
			pls_mensalidade_segurado	a
		where	trunc(dt_mesano_referencia, 'Month') = trunc(dt_mes_p,'Month')
		and	a.nr_seq_segurado	= b.nr_sequencia
		and	a.nr_seq_contrato	= nr_seq_contrato_p;
	end if;
elsif (ie_tipo_valor_p	= 'AC') then
	if (coalesce(nr_seq_contrato_p,0) = 0) then
		select	coalesce(sum(vl_mensalidade),0) vl_receita
		into STRICT	vl_retorno_w
		from	pls_mensalidade_segurado
		where	dt_mesano_referencia between trunc(dt_mes_p,'yyyy') and last_day(dt_mes_p);
	elsif (coalesce(nr_seq_contrato_p,0) > 0) then
		select	coalesce(sum(vl_mensalidade),0) vl_receita
		into STRICT	vl_retorno_w
		from	pls_segurado			b,
			pls_mensalidade_segurado	a
		where	dt_mesano_referencia between trunc(dt_mes_p,'yyyy') and last_day(dt_mes_p)
		and	a.nr_seq_segurado	= b.nr_sequencia
		and	a.nr_seq_contrato	= nr_seq_contrato_p;
	end if;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
