CREATE OR REPLACE FUNCTION pls_obter_valor_receita ( cd_ano_p bigint, ie_tipo_p bigint, dt_mes_p timestamp) RETURNS bigint AS $body$
DECLARE

/*
	1 = Valor receita
	2 = Valor inadimplencia
*/
ds_retorno_w			double precision;


BEGIN

if (ie_tipo_p = 1) then
	select	coalesce(sum(pls_obter_valor_item_mens(a.nr_sequencia,'1')),0) + coalesce(sum(pls_obter_valor_item_mens(a.nr_sequencia,'6')),0) valor_receita
	into STRICT	ds_retorno_w
	FROM mes_v b
LEFT OUTER JOIN pls_mensalidade_segurado a ON (b.dt_mes = a.dt_mesano_referencia)
LEFT OUTER JOIN pls_mensalidade d ON (a.nr_seq_mensalidade = d.nr_sequencia)
LEFT OUTER JOIN titulo_receber c ON (d.nr_sequencia = c.nr_seq_mensalidade)
WHERE to_char(b.dt_mes, 'YYYY') = cd_ano_p    and to_date(c.dt_liquidacao, 'DD/MM/YYYY')  <= to_date(coalesce(c.dt_pagamento_previsto,c.dt_vencimento), 'DD/MM/YYYY') and b.dt_mes = dt_mes_p;
elsif (ie_tipo_p = 2) then
	select	coalesce(sum(pls_obter_valor_item_mens(a.nr_sequencia,'1')),0) + coalesce(sum(pls_obter_valor_item_mens(a.nr_sequencia,'6')),0) valor_inadimp
	into STRICT	ds_retorno_w
	FROM mes_v b
LEFT OUTER JOIN pls_mensalidade_segurado a ON (b.dt_mes = a.dt_mesano_referencia)
LEFT OUTER JOIN pls_mensalidade d ON (a.nr_seq_mensalidade = d.nr_sequencia)
LEFT OUTER JOIN titulo_receber c ON (d.nr_sequencia = c.nr_seq_mensalidade)
WHERE to_char(b.dt_mes, 'YYYY') = cd_ano_p    and ((to_date(c.dt_liquidacao, 'DD/MM/YYYY') > to_date(coalesce(c.dt_pagamento_previsto,c.dt_vencimento), 'DD/MM/YYYY')) or (coalesce(to_date(c.dt_liquidacao, 'DD/MM/YYYY')::text, '') = '' and to_date(coalesce(c.dt_pagamento_previsto,c.dt_vencimento), 'DD/MM/YYYY') < to_date(clock_timestamp(), 'DD/MM/YYYY'))) and b.dt_mes = dt_mes_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
