SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valor_rec_proprio ( nr_seq_prestador_p bigint, dt_competencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_movimento_w			double precision	:= 0;
vl_retorno_w			double precision	:= 0;
cd_conta_contabil_w		varchar(20);
cd_centro_custo_w		integer;
ie_tipo_w			varchar(1);
cd_classificacao_w		varchar(40);
cd_estabelecimento_w		pls_cc_recurso_proprio.cd_estabelecimento%type;

C01 CURSOR FOR
	SELECT	cd_conta_contabil,
		cd_centro_custo,
		cd_estabelecimento
	from	pls_cc_recurso_proprio
	where	nr_seq_prestador	= nr_seq_prestador_p;


BEGIN
open C01;
loop
fetch C01 into
	cd_conta_contabil_w,
	cd_centro_custo_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	ie_tipo,
		cd_classificacao
	into STRICT	ie_tipo_w,
		cd_classificacao_w
	from	conta_contabil
	where	cd_conta_contabil	= cd_conta_contabil_w;

	if (ie_tipo_w	= 'A') then
		select	sum(coalesce(a.vl_debito,0)) - sum(coalesce(a.vl_credito,0))
		into STRICT	vl_movimento_w
		from	ctb_mes_ref	b,
			ctb_saldo	a
		where	trunc(b.dt_referencia, 'month')	= trunc(dt_competencia_p, 'month')
		and	a.cd_estabelecimento		= cd_estabelecimento_w
		and	a.nr_seq_mes_ref		= b.nr_sequencia
		and	a.cd_conta_contabil		= cd_conta_contabil_w
		and	coalesce(a.cd_centro_custo,0)	= coalesce(cd_centro_custo_w,0);
	elsif (ie_tipo_w	= 'T') then

		if (coalesce(cd_centro_custo_w,0) = 0) then
			select	sum(coalesce(a.vl_debito,0)) - sum(coalesce(a.vl_credito,0))
			into STRICT	vl_movimento_w
			from	ctb_mes_ref	b,
				ctb_saldo	a
			where	trunc(b.dt_referencia, 'month')	= trunc(dt_competencia_p, 'month')
			and	a.cd_estabelecimento		= cd_estabelecimento_w
			and	a.nr_seq_mes_ref		= b.nr_sequencia
			and	a.cd_conta_contabil		= cd_conta_contabil_w
			--and	substr(ctb_obter_se_classif_sup(a.cd_classificacao, cd_classificacao_w),1,1) = 'S'
			and (substr(obter_se_conta_vigente(cd_conta_contabil_w, ctb_obter_mes_ref(a.nr_seq_mes_ref)),1,1) = 'S')
			and	coalesce(a.cd_centro_custo,0)	= coalesce(cd_centro_custo_w,0);
		else
			cd_classificacao_w	:= ctb_obter_classif_conta(cd_conta_contabil_w, cd_classificacao_w, clock_timestamp());

			select	sum(coalesce(a.vl_debito,0)) - sum(coalesce(a.vl_credito,0))
			into STRICT	vl_movimento_w
			from	ctb_mes_ref	b,
				ctb_saldo	a
			where	trunc(b.dt_referencia, 'month')	= trunc(dt_competencia_p, 'month')
			and	a.cd_estabelecimento		= cd_estabelecimento_w
			and	a.nr_seq_mes_ref		= b.nr_sequencia
			and	a.cd_classificacao		like cd_classificacao_w || '%'
			--and	substr(ctb_obter_se_classif_sup(a.cd_classificacao, cd_classificacao_w),1,1) = 'S'
			and (substr(obter_se_conta_vigente(cd_conta_contabil_w, ctb_obter_mes_ref(a.nr_seq_mes_ref)),1,1) = 'S')
			and	coalesce(a.cd_centro_custo,0)	= coalesce(cd_centro_custo_w,0);
		end if;
	end if;

	vl_retorno_w	:= vl_retorno_w + coalesce(vl_movimento_w,0);
	end;
end loop;
close C01;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
