SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valor_reembolso ( nr_seq_conta_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*ie opcao_p
	P - Pendente
	CTB - Contabil
	LQ - LÃ­quido
*/
vl_retorno_w			double precision;
vl_pendente_proc_w		double precision;
vl_pendente_mat_w		double precision;
vl_contabil_w			double precision;
vl_contabil_proc_w		double precision;
vl_contabil_mat_w		double precision;
vl_liquido_w			double precision;


BEGIN

/* Valores do procedimento */

select	coalesce(sum(CASE WHEN a.ie_status='L' THEN 0 WHEN a.ie_status='S' THEN 0  ELSE a.vl_procedimento_imp END ),0) vl_pendente
into STRICT	vl_pendente_proc_w
from	pls_conta_proc	a
where	a.nr_seq_conta	= nr_seq_conta_p;

/* Valores do material */

select	coalesce(sum(CASE WHEN a.ie_status='L' THEN 0 WHEN a.ie_status='S' THEN 0  ELSE a.vl_material_imp END ),0) vl_pendente
into STRICT	vl_pendente_mat_w
from	pls_conta_mat	a
where	a.nr_seq_conta	= nr_seq_conta_p;

/* Valores da conta */

--select	nvl(sum(nvl(a.vl_total,0) + nvl(a.vl_coparticipacao,0) + nvl(a.vl_coparticipacao,0) + nvl(a.vl_glosa,0) + nvl(a.vl_glosa,0)),0) vl_contabil
select	coalesce(sum(coalesce(c.vl_liberado,0) + coalesce(c.vl_glosa,0)),0) vl_contabil
into STRICT	vl_contabil_mat_w
from	pls_conta_mat		c,
	pls_conta		a,
	pls_protocolo_conta	b
where	b.nr_sequencia	= a.nr_seq_protocolo
and	a.nr_sequencia	= c.nr_seq_conta
and	a.nr_sequencia	= nr_seq_conta_p
and	b.ie_status in ('3','4','6');


select	coalesce(sum(coalesce(c.vl_liberado,0) + coalesce(c.vl_coparticipacao,0)+ coalesce(c.vl_glosa,0)),0) vl_contabil
into STRICT	vl_contabil_proc_w
from	pls_conta_proc		c,
	pls_conta		a,
	pls_protocolo_conta	b
where	b.nr_sequencia	= a.nr_seq_protocolo
and	a.nr_sequencia	= c.nr_seq_conta
and	a.nr_sequencia	= nr_seq_conta_p
and	b.ie_status in ('3','4','6');

/*select	nvl(sum(nvl(a.vl_total,0) + nvl(a.vl_coparticipacao,0) + nvl(a.vl_coparticipacao,0) + nvl(a.vl_glosa,0) + nvl(a.vl_glosa,0)),0) vl_contabil
into	vl_contabil_w
from	pls_conta		a,
	pls_protocolo_conta	b
where	b.nr_sequencia	= a.nr_seq_protocolo
and	a.nr_sequencia	= nr_seq_conta_p
and	b.ie_status in ('3','4','6');*/
vl_contabil_w	:= vl_contabil_mat_w + vl_contabil_proc_w;

if (ie_opcao_p	= 'P') then
	vl_retorno_w	:= vl_pendente_proc_w + vl_pendente_mat_w;
elsif (ie_opcao_p	= 'CTB') then
	vl_retorno_w	:= vl_contabil_w;
elsif (ie_opcao_p	= 'LQ') then
	select	sum(coalesce(a.vl_total,0))
	into STRICT	vl_liquido_w
	from	pls_conta	a
	where	a.nr_sequencia = nr_seq_conta_p;

	vl_retorno_w := vl_liquido_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
