CREATE OR REPLACE FUNCTION pls_obter_valor_regra_copartic (nr_seq_regra_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


vl_copartic_w	double precision;


BEGIN

if (ie_tipo_p = 'A') then
	select	max(vl_coparticipacao)
	into STRICT	vl_copartic_w
	from	pls_regra_coparticipacao
	where	nr_sequencia	= nr_seq_regra_p;
elsif (ie_tipo_p = 'R') then
	select	max(vl_coparticipacao)
	into STRICT	vl_copartic_w
	from	pls_regra_coparticipacao
	where	nr_sequencia	= nr_seq_regra_p;
end if;

return	vl_copartic_w;

end;
$body$
