CREATE OR REPLACE FUNCTION pls_obter_valor_repasse ( nr_seq_repasse_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*	ie_opcao_p
	'R' - Repasse
	'B' - Valor base
*/
vl_repasse_w		double precision;
vl_retorno_w		double precision;
vl_adic_mensal_w	double precision;
vl_base_w		double precision;


BEGIN

/*select	nvl(vl_adic_mensal,0)
into	vl_adic_mensal_w
from	pls_repasse_vend
where	nr_sequencia = nr_seq_repasse_p;
*/
if (ie_opcao_p = 'R') then
	select	sum(a.vl_repasse)
	into STRICT	vl_repasse_w
	from	pls_repasse_mens a
	where	a.nr_seq_repasse = nr_seq_repasse_p;

	vl_retorno_w	:= coalesce(vl_repasse_w,0);
elsif (ie_opcao_p = 'B') then
	select	sum(b.vl_mensalidade)
	into STRICT	vl_base_w
	from	pls_repasse_mens a,
		pls_mensalidade_segurado b
	where	a.nr_seq_mens_seg	= b.nr_sequencia
	and	a.nr_seq_repasse	= nr_seq_repasse_p;

	vl_retorno_w	:= coalesce(vl_base_w,0);
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
