SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valor_resultado ( nr_seq_resultado_p bigint, ie_tipo_valor_p bigint) RETURNS bigint AS $body$
DECLARE


/* IE_TIPO_VALOR_P
	1	|Mensalidade
	2	|Contas médicas
	3	|Reembolso
	4	|Comissão vendas
	5	|Despesas não assistênciais
	6	|Encargos
	7	|Ressarcimento SUS
	8	|Resultado
*/
ie_tipo_valor_w			varchar(5);
vl_resultado_w			double precision;


BEGIN

select	ie_tipo_valor,
	vl_resultado
into STRICT	ie_tipo_valor_w,
	vl_resultado_w
from	pls_resultado
where	nr_sequencia	= nr_seq_resultado_p
and	((ie_tipo_valor	= ie_tipo_valor_p) or (ie_tipo_valor_p = 8));

if (ie_tipo_valor_w	not in (1,11)) then
	vl_resultado_w		:= (abs(vl_resultado_w) * -1);
end if;

return	vl_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
