CREATE OR REPLACE FUNCTION pls_obter_valor_sca_proposta ( nr_seq_proposta_p bigint, nr_seq_prop_benef_p bigint, nr_seq_sca_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision := 0;
nr_seq_tabela_sca_w	double precision;
cd_beneficiario_w	varchar(20);
qt_idade_w		smallint;
nr_seq_segurado_w	bigint;
vl_sca_w		double precision := 0;
ie_grau_parentesco_w	varchar(20);

C01 CURSOR FOR
	SELECT	b.nr_sequencia,
		a.nr_seq_tabela,
		b.cd_beneficiario
	from	pls_sca_vinculo			a,
		pls_proposta_beneficiario	b
	where	a.nr_seq_benef_proposta	= b.nr_sequencia
	and	((b.nr_seq_proposta = coalesce(nr_seq_proposta_p,0)) or (b.nr_sequencia = coalesce(nr_seq_prop_benef_p,0)))
	and	a.nr_seq_plano	= nr_seq_sca_p
	and	coalesce(b.dt_cancelamento::text, '') = '';

C02 CURSOR FOR
	SELECT	coalesce(vl_preco_atual,0)
	from	pls_plano_preco
	where	nr_seq_tabela	= nr_seq_tabela_sca_w
	and	qt_idade_w between qt_idade_inicial and qt_idade_final
	and	coalesce(ie_grau_titularidade,ie_grau_parentesco_w)	= ie_grau_parentesco_w
	order	by	coalesce(ie_grau_titularidade,' ');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_segurado_w,
	nr_seq_tabela_sca_w,
	cd_beneficiario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_grau_parentesco_w	:= coalesce(substr(pls_obter_garu_dependencia_seg(nr_seq_segurado_w,'P'),1,2),'X');

	select	substr(obter_idade_pf(cd_beneficiario_w,clock_timestamp(),'A'),1,3)
	into STRICT	qt_idade_w
	;

	open c02;
	loop
	fetch c02 into
		vl_sca_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
	end loop;
	close c02;

	vl_retorno_w	:= vl_retorno_w + vl_sca_w;
	end;
end loop;
close C01;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
