CREATE OR REPLACE FUNCTION pls_obter_valor_ultimo_reaj (nr_seq_faixa_etaria_p bigint) RETURNS bigint AS $body$
DECLARE


vl_reajuste_w		double precision	:= 0;
nr_seq_reajuste_w	bigint;

BEGIN

if (nr_seq_faixa_etaria_p <> 0) then
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_reajuste_w
	from	pls_reajuste_preco
	where	nr_seq_preco	= nr_seq_faixa_etaria_p
	and	dt_reajuste	= (	SELECT	max(dt_reajuste)
					from	pls_reajuste_preco
					where	nr_seq_preco	= nr_seq_faixa_etaria_p);
	if (nr_seq_reajuste_w > 0) then
		select	coalesce(vl_reajustado,0)
		into STRICT	vl_reajuste_w
		from	pls_reajuste_preco
		where	nr_sequencia	= nr_seq_reajuste_w;
	else
		select	coalesce(vl_preco_atual,vl_preco_inicial)
		into STRICT	vl_reajuste_w
		from	pls_plano_preco
		where	nr_sequencia	= nr_seq_faixa_etaria_p;
	end if;
end if;

return vl_reajuste_w;

end;
$body$
