CREATE OR REPLACE FUNCTION pls_obter_val_calc_conta ( nr_seq_conta_p pls_conta.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE

vl_calc_w	double precision;


BEGIN

select	sum(vl_calculado)
into STRICT	vl_calc_w
from (SELECT	sum(vl_procedimento) vl_calculado
	from	pls_conta_proc
	where 	nr_seq_conta = nr_seq_conta_p
	
union all

	SELECT	sum(vl_material) vl_calculado
	from	pls_conta_mat
	where 	nr_seq_conta = nr_seq_conta_p
	) alias3;

return	vl_calc_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
