CREATE OR REPLACE FUNCTION pls_obter_val_proc_utilizado (nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_limitacao_p pls_limitacao.nr_sequencia%type, nr_seq_plano_p pls_plano.nr_sequencia%type, ie_tipo_p text ) RETURNS bigint AS $body$
DECLARE

			
qt_total_w	bigint;


BEGIN

if (ie_tipo_p = 'T') then
	select	sum(tx_utilizada)
	into STRICT	qt_total_w
	from (
		SELECT	substr((pls_obter_qt_autorizacao_proc(nr_seq_segurado_p, a.nr_seq_limitacao, a.cd_procedimento, a.ie_periodo, a.ie_tipo_periodo, a.ie_origem_proced) * 100) / CASE WHEN coalesce(a.qt_permitida,0)=0 THEN 1  ELSE a.qt_permitida END , 1, 8) tx_utilizada
		from	pls_limitacao_autoriz_proc_v a
		where	a.nr_seq_contrato = 	nr_seq_contrato_p
		and	nr_seq_limitacao = nr_seq_limitacao_p
		and	((a.nr_seq_plano_contrato = 	nr_seq_plano_p) or (coalesce(a.nr_seq_plano_contrato::text, '') = ''))
		
union all

		SELECT	substr((pls_obter_qt_autorizacao_proc(nr_seq_segurado_p, a.nr_seq_limitacao, a.cd_procedimento, a.ie_periodo, a.ie_tipo_periodo, a.ie_origem_proced) * 100) / CASE WHEN coalesce(a.qt_permitida,0)=0 THEN 1  ELSE a.qt_permitida END , 1, 8) tx_utilizada
		from	pls_limitacao_autoriz_proc_v a
		where	a.nr_seq_plano = 	nr_seq_plano_p
		and	nr_seq_limitacao = nr_seq_limitacao_p
		and	not exists (	select 1 from	pls_limitacao where	nr_seq_contrato = 	nr_seq_contrato_p)) b
	where	1 = 1;
else
	select	sum(qt_utilizada)
	into STRICT	qt_total_w
	from (
		SELECT	pls_obter_qt_autorizacao_proc(nr_seq_segurado_p, a.nr_seq_limitacao, a.cd_procedimento, a.ie_periodo, a.ie_tipo_periodo, a.ie_origem_proced) qt_utilizada
		from	pls_limitacao_autoriz_proc_v a
		where	a.nr_seq_contrato = 	nr_seq_contrato_p
		and	nr_seq_limitacao = nr_seq_limitacao_p
		and	((a.nr_seq_plano_contrato = 	nr_seq_plano_p) or (coalesce(a.nr_seq_plano_contrato::text, '') = ''))
		
union all

		SELECT	pls_obter_qt_autorizacao_proc(nr_seq_segurado_p, a.nr_seq_limitacao, a.cd_procedimento, a.ie_periodo, a.ie_tipo_periodo, a.ie_origem_proced) qt_utilizada
		from	pls_limitacao_autoriz_proc_v a
		where	a.nr_seq_plano = 	nr_seq_plano_p
		and	nr_seq_limitacao = nr_seq_limitacao_p
		and	not exists (	select 1 from	pls_limitacao where	nr_seq_contrato = 	nr_seq_contrato_p)) b
	where	1 = 1;
	
end if;

return	qt_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
