CREATE OR REPLACE FUNCTION pls_obter_vendas_regra ( nr_seq_regra_p bigint, nr_seq_equipe_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


ds_retorno_w			bigint;
qt_vendas_w			bigint := 0;
nr_seq_mensalidade_seg_w	bigint;
ie_tipo_contrato_w		varchar(2);
nr_seq_plano_meta_w		bigint;
ie_acao_contrato_meta_w		varchar(255);
ie_acao_contrato_w		varchar(15);
nr_parcela_w			bigint;
nr_seq_grupo_produto_w		bigint;
dt_referencia_w			timestamp;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.ie_acao_contrato
	from	pls_segurado			a,
		pls_contrato			b
	where	a.nr_seq_contrato	= b.nr_sequencia
	--and	c.nr_seq_segurado	= a.nr_sequencia
	and exists (	SELECT	1
			from	pls_equipe_vend_vinculo	x
			where	x.nr_seq_equipe		= nr_seq_equipe_p
			and	x.nr_seq_vendedor	= a.nr_seq_vendedor_canal
			and	dt_referencia_w between coalesce(dt_inicio_vigencia,dt_referencia_w) and coalesce(dt_fim_vigencia,dt_referencia_w))
	and	trunc(a.dt_contratacao,'month') = dt_referencia_w
	and	((CASE WHEN coalesce(b.cd_pf_estipulante::text, '') = '' THEN 'PJ'  ELSE 'PF' END  = ie_tipo_contrato_w) or (ie_tipo_contrato_w = 'A'))
	and	((coalesce(nr_seq_grupo_produto_w::text, '') = '') or (exists (	select	1
								from	pls_preco_produto z
								where	z.nr_seq_plano	= a.nr_seq_plano
								and	z.nr_seq_grupo	= nr_seq_grupo_produto_w)))
	and	((a.nr_seq_plano = nr_seq_plano_meta_w) or (coalesce(nr_seq_plano_meta_w::text, '') = ''));


BEGIN

dt_referencia_w := trunc(dt_referencia_p,'month');

select	a.ie_tipo_contrato,
	a.nr_seq_plano,
	a.ie_acao_contrato,
	a.nr_seq_grupo_produto
into STRICT	ie_tipo_contrato_w,
	nr_seq_plano_meta_w,
	ie_acao_contrato_meta_w,
	nr_seq_grupo_produto_w
from	pls_equipe_vend_meta	a,
	pls_equipe_vendedor	b
where	a.nr_seq_equipe		= b.nr_sequencia
and	b.nr_sequencia		= nr_seq_equipe_p
and	a.nr_sequencia		= nr_seq_regra_p;

open C01;
loop
fetch C01 into
	nr_seq_mensalidade_seg_w,
	ie_acao_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if	((pls_obter_se_item_lista(ie_acao_contrato_meta_w,ie_acao_contrato_w) = 'S') or (coalesce(ie_acao_contrato_meta_w::text, '') = '')) then
		qt_vendas_w := qt_vendas_w + 1;
	end if;

	end;
end loop;
close C01;

ds_retorno_w := qt_vendas_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
