CREATE OR REPLACE FUNCTION pls_obter_versao_ptu ( cd_estabelecimento_p bigint, nr_seq_congenere_p bigint, dt_referencia_p timestamp, ie_tipo_ptu_p text) RETURNS varchar AS $body$
DECLARE


cd_versao_ptu_w		ptu_regra_interface.cd_versao_ptu%type	:= null;
dt_referencia_w		timestamp;

C01 CURSOR FOR
	SELECT	cd_versao_ptu
	from	ptu_regra_interface
	where	((nr_seq_congenere = nr_seq_congenere_p) or (coalesce(nr_seq_congenere::text, '') = ''))
	and	cd_estabelecimento = cd_estabelecimento_p
	and	dt_inicio_vigencia <= dt_referencia_w
	and	ie_tipo_ptu = ie_tipo_ptu_p
	order by dt_inicio_vigencia,
		coalesce(nr_seq_congenere,0);


BEGIN
dt_referencia_w	:= trunc(dt_referencia_p,'dd');

open C01;
loop
fetch C01 into
	cd_versao_ptu_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return cd_versao_ptu_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
