CREATE OR REPLACE FUNCTION pls_obter_versao_tiss () RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar a versão vigente do TISS
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_versao_w		pls_versao_tiss.cd_versao_tiss%type;


BEGIN

select	max(cd_versao_tiss)
into STRICT	cd_versao_w
from	pls_versao_tiss
where	dt_inicio_vigencia <= clock_timestamp()
and (coalesce(dt_fim_vigencia::text, '') = '' or dt_fim_vigencia > clock_timestamp());

return	cd_versao_w;

end;
