SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_via_cartao_interc ( nr_seq_segurado_p bigint) RETURNS bigint AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter a via do cartão do beneficiário
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_via_cartao_w			integer;
nr_seq_congenere_w		bigint;
ie_tipo_envio_via_w		bigint;


BEGIN

select	max(coalesce(nr_via_solicitacao,1))
into STRICT	nr_via_cartao_w
from	pls_segurado_carteira
where	nr_seq_segurado	= nr_seq_segurado_p;

	begin
		select	nr_seq_congenere
		into STRICT	nr_seq_congenere_w
		from	pls_segurado
		where	nr_sequencia	= nr_seq_segurado_p;
	exception
	when others then
		nr_seq_congenere_w	:= null;
	end;

select	max(ie_tipo_envio_via)
into STRICT	ie_tipo_envio_via_w
from	pls_regra_limite_envio_scs
where ((nr_seq_congenere IS NOT NULL AND nr_seq_congenere::text <> '')
or	nr_seq_congenere	= nr_seq_congenere_w);

if (ie_tipo_envio_via_w	= 2) then
	nr_via_cartao_w	:= 0;
end if;


return	nr_via_cartao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_via_cartao_interc ( nr_seq_segurado_p bigint) FROM PUBLIC;

