CREATE OR REPLACE FUNCTION pls_obter_vidas_contr_vend ( nr_seq_vendedor_p bigint, nr_seq_contrato_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(25);


BEGIN

select	count(1)
into STRICT	ds_retorno_w
from	pls_segurado
where	nr_seq_vendedor_canal	= nr_seq_vendedor_p
and	nr_seq_contrato		= nr_seq_contrato_p
and	trunc(dt_contratacao,'month')	= trunc(dt_referencia_p,'month');

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
