CREATE OR REPLACE FUNCTION pls_obter_vidas_subestipulante ( nr_seq_substipulante_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE

/* IE_TIPO_RETORNO_P
	A - Ativo
	I - Inativo
*/
ds_retorno_w			bigint;

BEGIN

if (ie_tipo_retorno_p	= 'A') then
	/* Obter a quantidade de vidas ativas */

	select	count(*)
	into STRICT	ds_retorno_w
	from	pls_segurado
	where	nr_seq_subestipulante = nr_seq_substipulante_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(dt_rescisao::text, '') = '';

elsif (ie_tipo_retorno_p	= 'I') then
	/*Obter a quantidade de vidas inativas*/

	select	count(*)
	into STRICT	ds_retorno_w
	from	pls_segurado
	where	nr_seq_subestipulante = nr_seq_substipulante_p
	and	(dt_rescisao IS NOT NULL AND dt_rescisao::text <> '');
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
