CREATE OR REPLACE FUNCTION pls_obter_vidas_vend_mes ( nr_seq_vendedor_p bigint, dt_mesano_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_retorno_w			integer	:= 0;


BEGIN

select	count(*) qt_vidas
into STRICT	qt_retorno_w
from	pls_repasse_vend	a,
	pls_repasse_mens	b
where	a.nr_sequencia		= b.nr_seq_repasse
and	a.ie_tipo_repasse	= 'M'
and	a.nr_seq_vendedor	= nr_seq_vendedor_p
and	trunc(a.dt_movimento,'month')	= trunc(dt_mesano_referencia_p,'month');

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
