SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_vinculos_contratuais ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
C - Obter todos os sub-contratos vinculados ao informado no parâmetro
P - Obter todos os contratos agrupados dos beneficiários do pagador informado no parâmetro
*/
nr_contrato_w		bigint;
ds_retorno_w		varchar(4000);

C01 CURSOR FOR
	SELECT	nr_contrato
	from	pls_contrato
	where	nr_contrato_principal	= nr_sequencia_p;

C02 CURSOR FOR
	SELECT	a.nr_contrato
	from	pls_segurado	b,
		pls_contrato	a
	where	b.nr_seq_contrato	= a.nr_sequencia
	and	b.nr_seq_pagador	= nr_sequencia_p
	and	not exists (	SELECT	1
				from	pls_contrato_pagador	x
				where	x.nr_sequencia		= nr_sequencia_p
				and	x.nr_seq_contrato	= a.nr_sequencia)
	group by a.nr_contrato;


BEGIN

if (ie_opcao_p = 'C') then
	open C01;
	loop
	fetch C01 into
		nr_contrato_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		ds_retorno_w	:= ds_retorno_w || to_char(nr_contrato_w) ||', ';

		end;
	end loop;
	close C01;
elsif (ie_opcao_p = 'P') then
	open C02;
	loop
	fetch C02 into
		nr_contrato_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		ds_retorno_w	:= ds_retorno_w || to_char(nr_contrato_w) ||', ';

		end;
	end loop;
	close C02;
end if;

ds_retorno_w	:= substr(ds_retorno_w,1,length(ds_retorno_w)-2);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
