CREATE OR REPLACE FUNCTION pls_obter_vl_apres_analise ( nr_seq_analise_p bigint ) RETURNS varchar AS $body$
DECLARE


vl_retorno_w		double precision := 0;
--vl_liberado_w		number(10,2);
--nr_seq_conta_w		number(10);
BEGIN

select	coalesce(sum(vl_cobrado),0)
into STRICT	vl_retorno_w
from	pls_conta
where 	nr_seq_analise = nr_seq_analise_p;
--vl_retorno_w := nvl(vl_retorno_w,0) + nvl(vl_liberado_w,0);
return Campo_Mascara_virgula_casas(coalesce(vl_retorno_w,0),2);

end;
$body$
