CREATE OR REPLACE FUNCTION pls_obter_vl_baixa_amortizacao (nr_seq_mensalidade_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_titulo_w	bigint;
vl_baixa_w	double precision;


BEGIN 
 
select	max(nr_titulo) 
into STRICT	nr_titulo_w 
from	titulo_receber 
where	nr_seq_mensalidade	= nr_seq_mensalidade_p;
 
select	sum(vl_recebido) 
into STRICT	vl_baixa_w 
from	titulo_receber_liq 
where	nr_titulo	= nr_titulo_w 
and	(nr_seq_amortizacao IS NOT NULL AND nr_seq_amortizacao::text <> '');
 
return	coalesce(vl_baixa_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
