SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_vl_calc_analise ( nr_seq_analise_p bigint ) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		pls_conta.vl_total%type;
vl_cobrado_proc_w	pls_conta_proc.vl_procedimento%type;
vl_cobrado_mat_w	pls_conta_mat.vl_material%type;

/*Obter as contas da analise*/

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta
	where 	nr_seq_analise = nr_seq_analise_p;

BEGIN
for r_c01_w in C01() loop
	begin

	select	sum(vl_procedimento)
	into STRICT	vl_cobrado_proc_w
	from	pls_conta_proc
	where	nr_seq_conta = r_c01_w.nr_sequencia;

	select	sum(vl_material)
	into STRICT	vl_cobrado_mat_w
	from	pls_conta_mat
	where	nr_seq_conta = r_c01_w.nr_sequencia;

	vl_retorno_w := coalesce(vl_retorno_w,0) + coalesce(vl_cobrado_proc_w,0) + coalesce(vl_cobrado_mat_w,0);
	end;
end loop;

if (coalesce(vl_retorno_w::text, '') = '') then
	vl_retorno_w := 0;
end if;
return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_vl_calc_analise ( nr_seq_analise_p bigint ) FROM PUBLIC;

