SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_vl_copart_rec_glosa (nr_seq_ref_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obtém o valor de coparticipação dos protocolos de origem dos lotes de recurso de glosa
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_protocolo_w	pls_protocolo_conta.nr_sequencia%type;
vl_coparticipacao_w	pls_protocolo_conta.vl_coparticipacao%type;
vl_coparticipacao_ww	pls_protocolo_conta.vl_coparticipacao%type;

C01 CURSOR FOR
	SELECT	a.nr_seq_conta
	from	pls_rec_glosa_conta 	a,
		pls_rec_glosa_protocolo	b
	where	b.nr_sequencia		= a.nr_seq_protocolo
	and	b.nr_sequencia 	= nr_seq_ref_p;

BEGIN
if (ie_opcao_p = 'P') then
	for r_C01_w in C01 loop
		begin
		select	coalesce(sum(vl_coparticipacao),0)
		into STRICT	vl_coparticipacao_w
		from	pls_conta_coparticipacao
		where	nr_seq_conta = r_C01_w.nr_seq_conta;

		vl_coparticipacao_ww := coalesce(vl_coparticipacao_ww,0) + vl_coparticipacao_w;
		end;
	end loop;

elsif (ie_opcao_p = 'C') then
	select	coalesce(sum(vl_coparticipacao),0)
	into STRICT	vl_coparticipacao_ww
	from	pls_conta_coparticipacao
	where	nr_seq_conta = nr_seq_ref_p;
elsif (ie_opcao_p = 'PROC') then
	select	coalesce(sum(vl_coparticipacao),0)
	into STRICT	vl_coparticipacao_ww
	from	pls_conta_coparticipacao
	where	nr_seq_proc_rec = nr_seq_ref_p;
elsif (ie_opcao_p = 'MAT') then
	select	coalesce(sum(vl_coparticipacao),0)
	into STRICT	vl_coparticipacao_ww
	from	pls_conta_coparticipacao
	where	nr_seq_mat_rec = nr_seq_ref_p;
end if;

return	vl_coparticipacao_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_vl_copart_rec_glosa (nr_seq_ref_p bigint, ie_opcao_p text) FROM PUBLIC;

