CREATE OR REPLACE FUNCTION pls_obter_vl_diops_tit_conta ( nr_titulo_p bigint, cd_conta_contabil_p text, nr_lote_contabil_p bigint, nr_seq_periodo_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	double precision	:= 0;


BEGIN

if (nr_seq_periodo_p IS NOT NULL AND nr_seq_periodo_p::text <> '') then
	select 	sum(CASE WHEN ie_idade_saldo_ativo=1 THEN  vl_individual WHEN ie_idade_saldo_ativo=2 THEN  vl_coletivo_pre WHEN ie_idade_saldo_ativo=3 THEN  vl_coletivo_pos WHEN ie_idade_saldo_ativo=4 THEN  vl_taxa_adm WHEN ie_idade_saldo_ativo=5 THEN  vl_credito_operadoras WHEN ie_idade_saldo_ativo=6 THEN  vl_outros_creditos WHEN ie_idade_saldo_ativo=7 THEN  vl_individual_pos WHEN ie_idade_saldo_ativo=8 THEN  vl_conv_receber WHEN ie_idade_saldo_ativo=9 THEN  vl_part_benefes WHEN ie_idade_saldo_ativo=10 THEN  vl_outros_creditos_sem_plano END ) vl_diops
	into STRICT	vl_retorno_w
	from  	w_diops_fin_idadesaldo_ati a
	where  	cd_conta_contabil	= cd_conta_contabil_p
	and	nr_titulo		= nr_titulo_p
	and	a.nr_seq_periodo	= nr_seq_periodo_p;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
