CREATE OR REPLACE FUNCTION pls_obter_vl_itens_conta ( nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, nr_seq_conta_p bigint ) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;


BEGIN

if (coalesce(nr_seq_conta_proc_p,0) > 0) then

	select	max(vl_procedimento_imp)
	into STRICT	vl_retorno_w
	from	pls_conta_proc
	where	nr_sequencia = nr_seq_conta_proc_p;

elsif (coalesce(nr_seq_conta_mat_p,0) > 0) then

	select	max(vl_material_imp)
	into STRICT	vl_retorno_w
	from	pls_conta_mat
	where	nr_sequencia = nr_seq_conta_mat_p;

elsif (coalesce(nr_seq_conta_p,0) > 0) then

	select	max(vl_cobrado)
	into STRICT	vl_retorno_w
	from	pls_conta
	where	nr_sequencia = nr_seq_conta_p;

end if;

vl_retorno_w := coalesce(vl_retorno_w,0);

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
