CREATE OR REPLACE FUNCTION pls_obter_vl_sca_embutido_pre ( nr_seq_segurado_p bigint, qt_idade_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
vl_preco_aux_w		double precision;
vl_preco_w		double precision;
nr_seq_plano_w		bigint;
nr_seq_tabela_w		bigint;
qt_idade_inicial_w	bigint;
qt_idade_final_w	bigint;

C01 CURSOR FOR
	SELECT	b.nr_seq_tabela
	from	pls_sca_vinculo	b,
		pls_plano	a
	where	b.nr_seq_plano		= a.nr_sequencia
	and	b.nr_seq_segurado	= nr_seq_segurado_p
	and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	and	((coalesce(dt_fim_vigencia::text, '') = '') or (dt_fim_vigencia > clock_timestamp()))
	and	coalesce(b.ie_lancamento_mensalidade,a.ie_lancamento_mensalidade)	= 'C';


BEGIN

vl_preco_w	:= 0;

open C01;
loop
fetch C01 into
	nr_seq_tabela_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(vl_preco_atual)
	into STRICT	vl_preco_aux_w
	from	pls_plano_preco
	where	nr_seq_tabela		= nr_seq_tabela_w
	and	qt_idade_p	between qt_idade_inicial and qt_idade_final;

	vl_preco_w	:= vl_preco_w + coalesce(vl_preco_aux_w,0);

	end;
end loop;
close C01;

vl_retorno_w	:= vl_preco_w;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
