SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_vl_sca_embut_simul ( nr_seq_item_simul_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;
vl_preco_aux_w			double precision;
qt_idade_w			bigint;
nr_seq_plano_w			bigint;
nr_seq_tabela_w			bigint;
ie_tipo_benef_w			varchar(10);
nr_seq_simulacao_w		pls_simulpreco_individual.nr_seq_simulacao%type;
qt_vidas_simul_indiv_w		integer;
ie_preco_vidas_contrato_w	pls_tabela_preco.ie_preco_vidas_contrato%type;
ie_calculo_vidas_w		pls_tabela_preco.ie_calculo_vidas%type;

C01 CURSOR FOR
	SELECT	nr_seq_tabela
	from	pls_plano_servico_adic
	where	nr_seq_plano = nr_seq_plano_w
	and	((coalesce(dt_fim_vigencia::text, '') = '') or (dt_fim_vigencia > clock_timestamp()))
	and	((ie_grau_dependencia = ie_tipo_benef_w) or (ie_grau_dependencia = 'A'))
	and	ie_lancamento_mensalidade = 'C';


BEGIN

select	qt_idade,
	nr_seq_produto,
	ie_tipo_benef,
	nr_seq_simulacao
into STRICT	qt_idade_w,
	nr_seq_plano_w,
	ie_tipo_benef_w,
	nr_seq_simulacao_w
from	pls_simulpreco_individual
where	nr_sequencia	= nr_seq_item_simul_p;

vl_retorno_w	:= 0;

open C01;
loop
fetch C01 into
	nr_seq_tabela_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	begin
	select	coalesce(ie_preco_vidas_contrato,'N'),
		coalesce(ie_calculo_vidas,'A')
	into STRICT	ie_preco_vidas_contrato_w,
		ie_calculo_vidas_w
	from	pls_tabela_preco
	where	nr_sequencia = nr_seq_tabela_w;
	exception
	when others then
		ie_preco_vidas_contrato_w	:= 'N';
		ie_calculo_vidas_w		:= 'A';
	end;
	
	if (ie_preco_vidas_contrato_w = 'S') then
		qt_vidas_simul_indiv_w := 0;
		
		if (ie_calculo_vidas_w in ('A','F')) then
			select	count(1)
			into STRICT	qt_vidas_simul_indiv_w
			from	pls_simulpreco_individual
			where	nr_seq_simulacao = nr_seq_simulacao_w;
		elsif (ie_calculo_vidas_w = 'T') then
			select	count(1)
			into STRICT	qt_vidas_simul_indiv_w
			from	pls_simulpreco_individual
			where	nr_seq_simulacao = nr_seq_simulacao_w
			and	ie_tipo_benef = 'T';
		elsif (ie_calculo_vidas_w = 'D') then
			select	count(1)
			into STRICT	qt_vidas_simul_indiv_w
			from	pls_simulpreco_individual
			where	nr_seq_simulacao = nr_seq_simulacao_w
			and	ie_tipo_benef = 'D';
		elsif (ie_calculo_vidas_w = 'TD') then
			select	count(1)
			into STRICT	qt_vidas_simul_indiv_w
			from	pls_simulpreco_individual a
			where	nr_seq_simulacao = nr_seq_simulacao_w
			and	((ie_tipo_benef = 'T') or ((ie_tipo_benef = 'D') and ((	SELECT	count(1)
											from	grau_parentesco x
											where	x.nr_sequencia = a.nr_seq_parentesco
											and	x.ie_tipo_parentesco = '1') > 0)));
		end if;
	end if;
	
	select	max(vl_preco_atual)
	into STRICT	vl_preco_aux_w
	from	pls_plano_preco
	where	nr_seq_tabela = nr_seq_tabela_w
	and	qt_idade_w between qt_idade_inicial and qt_idade_final
	and	((ie_preco_vidas_contrato_w = 'S' and
		  qt_vidas_simul_indiv_w between coalesce(qt_vidas_inicial,qt_vidas_simul_indiv_w) and coalesce(qt_vidas_final,qt_vidas_simul_indiv_w)) or (ie_preco_vidas_contrato_w = 'N'));
	
	vl_retorno_w	:= vl_retorno_w + coalesce(vl_preco_aux_w,0);
	
	end;
end loop;
close C01;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_vl_sca_embut_simul ( nr_seq_item_simul_p bigint) FROM PUBLIC;

