CREATE OR REPLACE FUNCTION pls_obter_vl_seg_relat_517 ( nr_seq_segurado_p bigint) RETURNS bigint AS $body$
DECLARE

ds_retorno_w	double precision;

BEGIN
select	coalesce(pls_obter_valor_segurado(a.nr_sequencia,'VCD'),0.00) - coalesce((select	sum(pls_obter_vl_bonif_seg_pre(a.nr_sequencia,b.nr_sequencia))
									from	pls_bonificacao_vinculo	b
									where	b.nr_seq_contrato	= a.nr_seq_contrato),0.00)
into STRICT	ds_retorno_w
from	pls_segurado	a
where	a.nr_sequencia	= nr_seq_segurado_p;
return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
