SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_vl_total_interc_aud ( nr_seq_auditoria_p bigint, nr_seq_proc_aud_p bigint) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Calcular o valor total para envio do intercâmbio
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_proc_origem_w		bigint;
nr_seq_pacote_w			bigint;
nr_seq_guia_w			bigint;
nr_seq_requisicao_w		bigint;
vl_retorno_w			double precision;



BEGIN

begin
	select	nr_seq_proc_origem
	into STRICT	nr_seq_proc_origem_w
	from	pls_auditoria_item
	where	nr_sequencia	= nr_seq_proc_aud_p;
exception
when others then
	nr_seq_proc_origem_w	:= null;
end;

begin
	select	nr_seq_guia,
		nr_seq_requisicao
	into STRICT	nr_seq_guia_w,
		nr_seq_requisicao_w
	from	pls_auditoria
	where	nr_sequencia	= nr_seq_auditoria_p;
exception
when others then
	nr_seq_guia_w		:= null;
	nr_seq_requisicao_w	:= null;
end;

if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') and (nr_seq_proc_origem_w IS NOT NULL AND nr_seq_proc_origem_w::text <> '') then
	begin
		select	nr_seq_pacote
		into STRICT	nr_seq_pacote_w
		from	pls_guia_plano_proc
		where	nr_sequencia	= nr_seq_proc_origem_w;
	exception
	when others then
		nr_seq_pacote_w	:= null;
	end;

	if (nr_seq_pacote_w IS NOT NULL AND nr_seq_pacote_w::text <> '') then
		begin
			select	vl_item
			into STRICT	vl_retorno_w
			from	pls_auditoria_item
			where	nr_sequencia	= nr_seq_proc_aud_p;
		exception
		when others then
			vl_retorno_w	:= 0;
		end;
	else
		begin
			select (vl_item * qt_ajuste)
			into STRICT	vl_retorno_w
			from	pls_auditoria_item
			where	nr_sequencia	= nr_seq_proc_aud_p;
		exception
		when others then
			vl_retorno_w	:= 0;
		end;
	end if;
elsif (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') and (nr_seq_proc_origem_w IS NOT NULL AND nr_seq_proc_origem_w::text <> '') then
	begin
		select	nr_seq_pacote
		into STRICT	nr_seq_pacote_w
		from	pls_requisicao_proc
		where	nr_sequencia	= nr_seq_proc_origem_w;
	exception
	when others then
		nr_seq_pacote_w	:= null;
	end;

	if (nr_seq_pacote_w IS NOT NULL AND nr_seq_pacote_w::text <> '') then
		begin
			select	vl_item
			into STRICT	vl_retorno_w
			from	pls_auditoria_item
			where	nr_sequencia	= nr_seq_proc_aud_p;
		exception
		when others then
			vl_retorno_w	:= 0;
		end;
	else
		begin
			select (vl_item * qt_ajuste)
			into STRICT	vl_retorno_w
			from	pls_auditoria_item
			where	nr_sequencia	= nr_seq_proc_aud_p;
		exception
		when others then
			vl_retorno_w	:= 0;
		end;
	end if;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_vl_total_interc_aud ( nr_seq_auditoria_p bigint, nr_seq_proc_aud_p bigint) FROM PUBLIC;

