CREATE OR REPLACE FUNCTION pls_obter_vl_total_orcamento ( nr_seq_orcamento_p bigint) RETURNS bigint AS $body$
DECLARE


vl_total_w			double precision;
vl_procedimento_w		double precision;
vl_material_w			double precision;


BEGIN

select	sum(qt_procedimento * vl_procedimento)
into STRICT	vl_procedimento_w
from	pls_orcamento_proc
where	nr_seq_orcamento	= nr_seq_orcamento_p;

select	sum(qt_material * vl_material)
into STRICT	vl_material_w
from	pls_orcamento_mat
where	nr_seq_orcamento	= nr_seq_orcamento_p;

vl_total_w	:= coalesce(vl_procedimento_w,0) + coalesce(vl_material_w,0);

return	vl_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
