SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_xml_pend_imp_web (ie_status_p text, ie_tipo_xml_p text) RETURNS bigint AS $body$
DECLARE


qt_registros_w		bigint := 0;


BEGIN


if (ie_tipo_xml_p = 'IMPORTACAO') then
	select 	count(1)
	into STRICT	qt_registros_w
	from  	pls_xml_arquivo a
	where	a.ie_status = ie_status_p
	and	a.ie_tipo_processo in (1,2);
elsif (ie_tipo_xml_p = 'CONFIRMACAO') then
	select 	count(1)
	into STRICT	qt_registros_w
	from  	pls_xml_lote a
	where	a.ie_status = ie_status_p
	and	a.ie_tipo_processo in (1,2);
end if;

return	qt_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
