CREATE OR REPLACE FUNCTION pls_obt_dados_contrato_opsint ( nr_seq_contrato_p bigint, nr_seq_intercambio_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

				 
/* IE_TIPO_P 
	E - Estipulante 
*/
 
				 
ds_retorno_w			varchar(255);
nm_estipulante_w		varchar(255);


BEGIN 
 
if (coalesce(nr_seq_contrato_p,0) > 0) then 
	select	substr(obter_nome_pf_pj(cd_pf_estipulante, cd_cgc_estipulante),1,200) 
	into STRICT	nm_estipulante_w 
	from	pls_contrato 
	where	nr_sequencia	= nr_seq_contrato_p;
elsif (coalesce(nr_seq_intercambio_p,0) > 0) then 
	select	substr(obter_nome_pf_pj(cd_pessoa_fisica,cd_cgc),1,255) 
	into STRICT	nm_estipulante_w 
	from	pls_intercambio 
	where	nr_sequencia	= nr_seq_intercambio_p;
end if;
 
if (ie_tipo_p	= 'E') then 
	ds_retorno_w	:= nm_estipulante_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
