CREATE OR REPLACE FUNCTION pls_obt_porta_gerar_hist_atend ( nr_seq_atendimento_p bigint, ie_tipo_historico_p text, nm_usuario_receb_p text, nm_usuario_envio_p text, ds_menssagem_p text, cd_funcao_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_tempo_w	bigint;
nr_porta_w	varchar(20);


BEGIN

select	round((clock_timestamp() - dt_atualizacao) * 86400,2)
into STRICT	qt_tempo_w
from	pls_atendimento_historico
where	nr_seq_tipo_historico	= 2
and	nr_seq_atendimento		= nr_seq_atendimento_p;

CALL pls_gerar_historico_atend(
	qt_tempo_w,
	nr_seq_atendimento_p,
	ie_tipo_historico_p,
	nm_usuario_receb_p,
	nm_usuario_envio_p,
	ds_menssagem_p);

select	nr_porta
into STRICT	nr_porta_w
from	usuario_conectado
where	cd_funcao		= cd_funcao_p
and	nm_usuario	= nm_usuario_p
and	coalesce(dt_fim_conexao::text, '') = '';

return	nr_porta_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
