CREATE OR REPLACE FUNCTION pls_obt_seguro_beneficio_obit ( nr_seq_segurado_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
S - Seguro de óbito
B - Benefício por óbito
*/
ie_retorno_w		varchar(10) := 'N';
qt_retorno_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_retorno_w
from	pls_segurado	c,
	pls_plano	b,
	pls_sca_vinculo	a
where	a.nr_seq_segurado	= c.nr_sequencia
and	a.nr_seq_plano		= b.nr_sequencia
and	c.nr_sequencia		= nr_seq_segurado_p
and	b.ie_seguro_obito	= ie_opcao_p;

if (qt_retorno_w = 0) and (ie_opcao_p = 'B') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	pls_segurado		c,
		pls_bonificacao		b,
		pls_bonificacao_vinculo	a
	where	a.nr_seq_segurado	= c.nr_sequencia
	and	a.nr_seq_bonificacao	= b.nr_sequencia
	and	c.nr_sequencia		= nr_seq_segurado_p
	and	b.IE_SEGURO_OBITO	= 'S';
end if;

if (qt_retorno_w	> 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
