CREATE OR REPLACE FUNCTION pls_oc_cta_obter_restr_val_46 ( ie_opcao_p text, nr_cursor_p integer, dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_id_transacao_p pls_oc_cta_selecao_ocor_v.nr_id_transacao%type, dados_validacao_p pls_tipos_ocor_pck.dados_val_qt_item) RETURNS PLS_TIPOS_OCOR_PCK.DADOS_RESTRICAO_SELECT AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Montar a restrição e atualizar os binds utilizados para montar o select dinâmico
	da validação 46 - Validar quntidade de itens.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Alterações:
-------------------------------------------------------------------------------------------------------------------
jjung OS 643681 - 19/09/2013 - Criação da function
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
dados_retorno_w		pls_tipos_ocor_pck.dados_restricao_select;


BEGIN

-- Quando for para atualizar as binds  deve ser atualizado o valor da bind de transacao que já vem por padrão no select.
if (ie_opcao_p <> 'RESTRICAO') then

	if (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '') then

		dbms_sql.bind_variable(nr_cursor_p, ':nr_id_transacao', nr_id_transacao_p);
	end if;
end if;

-- Se tiver apenas a quantidade mínima informada então temos que buscar os itens que estão com quantidade menor informada para que possamos gerar a ocorrência para estes itens.
if ((dados_validacao_p.qt_minima IS NOT NULL AND dados_validacao_p.qt_minima::text <> '') and coalesce(dados_validacao_p.qt_maxima::text, '') = '') then

	--Verificar o momento, se é para montar a restrição ou atualizar o valor das binds.
	if (ie_opcao_p = 'RESTRICAO') then

		dados_retorno_w.ds_restricao_proc :=	dados_retorno_w.ds_restricao_proc || pls_tipos_ocor_pck.enter_w ||
							'and	proc.qt_procedimento_imp < :qt_minima ';

		dados_retorno_w.ds_restricao_mat :=	dados_retorno_w.ds_restricao_mat || pls_tipos_ocor_pck.enter_w ||
							'and	mat.qt_material_imp < :qt_minima ';
	else
		dbms_sql.bind_variable(nr_cursor_p, ':qt_minima', dados_validacao_p.qt_minima);
	end if;
end if;

-- Se tiver apenas a quantidade máxima informada então temos que buscar os itens que estão com quantidade maior informada para que possamos gerar a ocorrência para estes itens.
if ((dados_validacao_p.qt_maxima IS NOT NULL AND dados_validacao_p.qt_maxima::text <> '') and coalesce(dados_validacao_p.qt_minima::text, '') = '') then

	--Verificar o momento, se é para montar a restrição ou atualizar o valor das binds.
	if (ie_opcao_p = 'RESTRICAO') then

		dados_retorno_w.ds_restricao_proc :=	dados_retorno_w.ds_restricao_proc || pls_tipos_ocor_pck.enter_w ||
							'and	proc.qt_procedimento_imp > :qt_maxima ';

		dados_retorno_w.ds_restricao_mat :=	dados_retorno_w.ds_restricao_mat || pls_tipos_ocor_pck.enter_w ||
							'and	mat.qt_material_imp > :qt_maxima ';
	else
		dbms_sql.bind_variable(nr_cursor_p, ':qt_maxima', dados_validacao_p.qt_maxima);
	end if;
end if;

-- Quando tiver os dois informados então deve ser feita a verificação em cima de uma faixa de valores.
if (dados_validacao_p.qt_maxima IS NOT NULL AND dados_validacao_p.qt_maxima::text <> '' AND dados_validacao_p.qt_minima IS NOT NULL AND dados_validacao_p.qt_minima::text <> '') then

	--Verificar o momento, se é para montar a restrição ou atualizar o valor das binds.
	if (ie_opcao_p = 'RESTRICAO') then

		dados_retorno_w.ds_restricao_proc :=	dados_retorno_w.ds_restricao_proc || pls_tipos_ocor_pck.enter_w ||
							'and	(proc.qt_procedimento_imp < :qt_minima or proc.qt_procedimento_imp > :qt_maxima) ';

		dados_retorno_w.ds_restricao_mat :=	dados_retorno_w.ds_restricao_mat || pls_tipos_ocor_pck.enter_w ||
							'and	(mat.qt_material_imp < :qt_minima or mat.qt_material_imp > :qt_maxima) ';
	else
		dbms_sql.bind_variable(nr_cursor_p, ':qt_maxima', dados_validacao_p.qt_maxima);
		dbms_sql.bind_variable(nr_cursor_p, ':qt_minima', dados_validacao_p.qt_minima);
	end if;
end if;

return	dados_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
