CREATE OR REPLACE FUNCTION pls_oc_cta_obter_val_sexo_exc ( dados_regra_p pls_tipos_ocor_pck.dados_regra, nr_seq_conta_proc_p bigint) RETURNS PLS_TIPOS_OCOR_PCK.RET_VALID AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se a conta ou item se encaixam na regra de validação associada
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
TIPO DE RETORNO - Tipo específico de package, retornar sempre todos os campos
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
retorno_w		pls_tipos_ocor_pck.ret_valid;
ie_valida_sexo_proc_w	varchar(1);
nr_seq_validacao_w	bigint;
ie_sexo_w		varchar(3);
ie_sexo_exclusivo_w	varchar(3);
nr_seq_segurado_w	bigint;


BEGIN

if (dados_regra_p.nr_sequencia > 0) and (nr_seq_conta_proc_p > 0) then

	select	max(a.nr_sequencia)
	into STRICT	nr_seq_validacao_w
	from	pls_oc_cta_val_sexo_proc a
	where	a.nr_seq_oc_cta_comb	= dados_regra_p.nr_sequencia;

	if (nr_seq_validacao_w IS NOT NULL AND nr_seq_validacao_w::text <> '') then
		select	a.ie_valida_sexo_proc
		into STRICT	ie_valida_sexo_proc_w
		from	pls_oc_cta_val_sexo_proc a
		where	a.nr_sequencia	= nr_seq_validacao_w;

		select	b.nr_seq_segurado
		into STRICT	nr_seq_segurado_w
		from	pls_conta b,
			pls_conta_proc a
		where	a.nr_seq_conta	= b.nr_sequencia
		and	a.nr_sequencia	= nr_seq_conta_proc_p;

		if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
			select	b.ie_sexo
			into STRICT	ie_sexo_w
			from	pessoa_fisica b,
				pls_segurado a
			where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
			and	a.nr_sequencia		= nr_seq_segurado_w;
		end if;

		select	max(b.ie_sexo_sus)
		into STRICT	ie_sexo_exclusivo_w
		from	procedimento b,
			pls_conta_proc a
		where	a.cd_procedimento	= b.cd_procedimento
		and	a.ie_origem_proced	= b.ie_origem_proced
		and	a.nr_sequencia		= nr_seq_conta_proc_p;
	end if;

	retorno_w.nr_sequencia		:= null;
	retorno_w.ie_aplicavel		:= 'N';
	retorno_w.ie_gera_ocorrencia	:= 'N';

	if (coalesce(ie_valida_sexo_proc_w,'S') = 'S') then
		if (ie_sexo_exclusivo_w = 'M') and (coalesce(ie_sexo_w,'M') <> 'M') then
			retorno_w.nr_sequencia		:= nr_seq_validacao_w;
			retorno_w.ie_aplicavel		:= 'S';
			retorno_w.ie_gera_ocorrencia	:= 'S';
		end if;
	end if;
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
