CREATE OR REPLACE FUNCTION pls_plano_obter_complemento ( nr_seq_plano_p bigint, ie_tipo_complemento_p text) RETURNS varchar AS $body$
DECLARE


/* IE_TIPO_COMPLEMENTO
	D - Descrição do produto
*/
ds_retorno_w			varchar(4000);
ds_descricao_plano_w		varchar(4000);


BEGIN

select	ds_descricao_plano
into STRICT	ds_descricao_plano_w
from	pls_plano_complemento
where	nr_seq_plano	= nr_seq_plano_p
and	nr_sequencia	= (	SELECT	max(nr_sequencia)
				from 	pls_plano_complemento
				where 	clock_timestamp() between dt_inicio_vigencia and coalesce(dt_fim_vigencia,clock_timestamp() + interval '30 days')
				and	nr_seq_plano = nr_seq_plano_p);

if (ie_tipo_complemento_p	= 'D') then
	ds_retorno_w	:= ds_descricao_plano_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
