CREATE OR REPLACE FUNCTION pls_pode_liberar_nova_geracao ( nr_seq_lote_p pls_cta_lote_processo.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_status_w	pls_cta_lote_processo.ie_status%type;
nm_id_job_w	pls_cta_lote_processo.nm_id_job%type;
ie_libera_w	varchar(1);
qt_registro_w	integer;


BEGIN
ie_libera_w := 'N';

select	max(ie_status),
	max(nm_id_job)
into STRICT	ie_status_w,
	nm_id_job_w
from	pls_cta_lote_processo
where	nr_sequencia = nr_seq_lote_p;

-- se for nulo ou em processamento, libera direto
if (coalesce(ie_status_w::text, '') = '' or ie_status_w != 2) then
	ie_libera_w := 'S';
end if;

-- se não está liberado ainda
if (ie_libera_w = 'N') then

	-- se este comando retornar registros, significa que o job foi executado
	select	count(1)
	into STRICT	qt_registro_w
	from 	job_v
	where 	job = nm_id_job_w
	and ((falhas IS NOT NULL AND falhas::text <> '') or parada = 'Y');

	if (qt_registro_w > 0) then
		-- se o job já foi executado, libera
		ie_libera_w := 'S';
	else
		-- verifica se o job existe
		select	count(1)
		into STRICT	qt_registro_w
		from 	job_v
		where 	job = nm_id_job_w;

		if (qt_registro_w = 0) then
			-- se o job não existe mais libera direto também
			ie_libera_w := 'S';
		end if;
	end if;
end if;

return ie_libera_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
