CREATE OR REPLACE FUNCTION pls_portab_obter_dados_consist ( nr_seq_inconsistencia_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/* IE_RETORNO_P
	C - Código
	D - Descrição
	A - Ação usuário
*/
cd_inconsistencia_w		smallint;
ds_inconsistencia_w		varchar(255);
ds_acao_usuario_w		varchar(2000);
ds_retorno_w			varchar(2000);


BEGIN

select	cd_inconsistencia,
	ds_inconsistencia,
	ds_acao_usuario
into STRICT	cd_inconsistencia_w,
	ds_inconsistencia_w,
	ds_acao_usuario_w
from	pls_portab_inconsistencia
where	nr_sequencia	= nr_seq_inconsistencia_p;

if (ie_retorno_p	= 'C') then
	ds_retorno_w	:= cd_inconsistencia_w;
elsif (ie_retorno_p	= 'D') then
	ds_retorno_w	:= ds_inconsistencia_w;
elsif (ie_retorno_p	= 'A') then
	ds_retorno_w	:= ds_acao_usuario_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
