SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_qt_benef_simulacao (nr_seq_simulacao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_registros_w		bigint;
ie_tipo_contratacao_w	varchar(2);


BEGIN

select	max(ie_tipo_contratacao)
into STRICT	ie_tipo_contratacao_w
from	pls_simulacao_preco
where	nr_sequencia	= nr_seq_simulacao_p;

if (ie_tipo_contratacao_w	= 'I') then
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_simulpreco_individual a
	where	a.nr_seq_simulacao	= nr_seq_simulacao_p;
elsif (ie_tipo_contratacao_w	in ('CA','CE')) then
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_simulpreco_coletivo a
	where	a.nr_seq_simulacao	= nr_seq_simulacao_p;
end if;

return	qt_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
