CREATE OR REPLACE FUNCTION pls_qt_faixa_benef_propos ( nr_seq_plano_preco_p bigint, nr_seq_proposta_p bigint, nr_seq_plano_p bigint) RETURNS bigint AS $body$
DECLARE


qt_segurado_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_segurado_w
from	pls_plano				a,
		pls_tabela_preco 			b,
		pls_plano_preco 			c,
		pls_proposta_beneficiario	d,
		pessoa_fisica				e
where	b.nr_seq_plano		= a.nr_sequencia
and		d.nr_seq_plano		= a.nr_sequencia
and		c.nr_Seq_tabela		= b.nr_sequencia
and		d.nr_seq_tabela		= b.nr_sequencia
and		d.cd_beneficiario	= e.cd_pessoa_fisica
and		c.nr_sequencia		= nr_seq_plano_preco_p
and		d.nr_seq_proposta 	= nr_seq_proposta_p
and		d.nr_seq_plano		= nr_seq_plano_p
and		substr(obter_idade_pf(d.cd_beneficiario,clock_timestamp(),'A'),1,3) between c.qt_idade_inicial and c.qt_idade_final
and	coalesce(d.dt_cancelamento::text, '') = ''
group by c.qt_idade_inicial,c.nr_sequencia;

return	qt_segurado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
