CREATE OR REPLACE FUNCTION pls_qt_info_pagador ( nr_seq_pagador_p bigint, nr_seq_pagador_fin_p bigint) RETURNS varchar AS $body$
DECLARE


qt_dt_fim_vigencia_w	bigint;
ds_retorno_w		varchar(1);


BEGIN

select	count(*)
into STRICT	qt_dt_fim_vigencia_w
from	pls_contrato_pagador_fin	a,
	pls_contrato_pagador            b
where	a.nr_seq_pagador	= nr_seq_pagador_p
and	(dt_inicio_vigencia IS NOT NULL AND dt_inicio_vigencia::text <> '')
and	coalesce(dt_fim_vigencia::text, '') = ''
and	a.nr_seq_pagador	= b.nr_sequencia
and	a.nr_sequencia		<> nr_seq_pagador_fin_p;

if (qt_dt_fim_vigencia_w = 0 ) then
	ds_retorno_w	:= 'N';
else
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
