CREATE OR REPLACE FUNCTION pls_qt_metas_canal_vendas ( nr_seq_equipe_p bigint, nr_seq_vendedor_p bigint, dt_mes_competencia_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
G - Global
PF - Pessoa Fisica
PJ - Pessoa Juridica
*/
qt_registros_w		bigint := 0;
qt_canais_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_canais_w
from	pls_equipe_vend_vinculo
where	nr_seq_equipe	= nr_seq_equipe_p
and	dt_mes_competencia_p between coalesce(dt_inicio_vigencia,dt_mes_competencia_p) and coalesce(dt_fim_vigencia,dt_mes_competencia_p);

qt_registros_w	:= dividir(pls_obter_qt_meta_equipe(nr_seq_equipe_p,nr_seq_vendedor_p,dt_mes_competencia_p,ie_opcao_p),qt_canais_w);

return	qt_registros_w;

end;
$body$
