CREATE OR REPLACE FUNCTION pls_qt_sexo_benef_faixa_etaria ( nr_sequencia_p bigint, nr_seq_parentesco_p bigint, qt_idade_inicial_p bigint, qt_idade_final_p bigint, ie_tipo_opcao_p text, ie_tipo_sexo_p text) RETURNS bigint AS $body$
DECLARE

/*
ie_tipo_opcao_p
C - Contrato
P - Proposta de ades√£o
*/
/*
ie_tipo_sexo_p
M - Masculino
F - Feminino
*/
qt_retorno_w		bigint;


BEGIN

if (ie_tipo_opcao_p	= 'C') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	pessoa_fisica		c,
		pls_segurado		b,
		pls_contrato		a
	where	b.nr_seq_contrato	= a.nr_sequencia
	and	b.cd_pessoa_fisica	= c.cd_pessoa_fisica
	and	((coalesce(nr_seq_parentesco_p::text, '') = '' and coalesce(b.nr_seq_titular::text, '') = '') or ((nr_seq_parentesco_p IS NOT NULL AND nr_seq_parentesco_p::text <> '') and b.nr_seq_parentesco = nr_seq_parentesco_p))
	and	a.nr_sequencia		= nr_sequencia_p
	and	c.ie_sexo		= ie_tipo_sexo_p
	and	obter_idade_pf(c.cd_pessoa_fisica,clock_timestamp(),'A')	between qt_idade_inicial_p and qt_idade_final_p;
elsif (ie_tipo_opcao_p	= 'P') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	pessoa_fisica			c,
		pls_proposta_beneficiario	b,
		pls_proposta_adesao		a
	where	b.nr_seq_proposta	= a.nr_sequencia
	and	b.cd_beneficiario	= c.cd_pessoa_fisica
	and	((coalesce(nr_seq_parentesco_p::text, '') = '' and coalesce(b.nr_seq_titular::text, '') = '' and coalesce(b.nr_seq_titular_contrato::text, '') = '') or ((nr_seq_parentesco_p IS NOT NULL AND nr_seq_parentesco_p::text <> '') and b.nr_seq_parentesco = nr_seq_parentesco_p))
	and	a.nr_sequencia		= nr_sequencia_p
	and	c.ie_sexo		= ie_tipo_sexo_p
	and	obter_idade_pf(c.cd_pessoa_fisica,clock_timestamp(),'A')	between qt_idade_inicial_p and qt_idade_final_p;
end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
